mapper.select:

	
	select id
	from users 
	where id=#{id}


	
	select blogTitle,logoFile
	from blog 
	where id=#{id}

mapper.update:


		update blog 
		set blogTitle=#{blogTitle} , 
			logoFile=#{logoFile} 
		where id=#{id}

mapper.select:

	
	select count(*)
	from post
	where cateNo=#{cateNo}
